// Doctor images management using external URLs

// Doctor image mapping with professionally-hosted images
export const doctorImages: Record<string, string> = {
  // Using high-quality professional placeholder images
  'Dr. Basavaiah': 'data:image/jpeg;base64,',
  'Dr. Priya Sharma': 'data:image/jpeg;base64,',
  'Dr. Rajesh Kumar': 'data:image/jpeg;base64,',
};

/**
 * Get doctor image by name with fallback
 * @param doctorName The doctor's name
 * @returns The image URL for the doctor
 */
export const getDoctorImage = (doctorName: string): string => {
  // Try to get the specific doctor image
  if (doctorImages[doctorName]) {
    return doctorImages[doctorName];
  }
  
  // Fallbacks based on gender hints in the name
  if (doctorName.includes('Dr. Priya') || 
      doctorName.toLowerCase().includes('sharma') || 
      doctorName.toLowerCase().includes('devi') ||
      doctorName.toLowerCase().includes('female')) {
    return 'https://img.freepik.com/free-photo/woman-doctor-wearing-lab-coat-with-stethoscope-isolated_1303-29791.jpg';
  }
  
  // Default male doctor image fallback
  return 'https://img.freepik.com/free-photo/indian-doctor-portrait_23-2149611051.jpg';
};

/**
 * Check if an image URL is valid
 * @param url The image URL to check
 * @returns Promise resolving to true if image loads, false otherwise
 */
export const checkImageExists = (url: string): Promise<boolean> => {
  return new Promise((resolve) => {
    const img = new Image();
    img.onload = () => resolve(true);
    img.onerror = () => resolve(false);
    img.src = url;
  });
};

export default doctorImages;
